// Once you have created an account on http://sightengine.com, you will get your api_user id and an api_secret, set them here
var API_USER = "";
var API_SECRET = "";

var SightengineClient = require ('./sightengine');
var Sightengine = new SightengineClient(API_USER, API_SECRET);

// Option 1
// Moderate an image that is publicly available over HTTP
var imageurl = "http://url.to/image.jpg";
Sightengine.checkNudityForURL(imageurl, function(error, result) {
  if(error) console.dir(error);
  else console.log(result);
});

// Option 2
// Moderate an image that is available on your local filesystem
var imagepath = "/path/to/image.jpg";
Sightengine.checkNudityForFile(imagepath, function(error, result) {
  if(error) console.dir(error);
  else console.log(result);
});

